import { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/navbar/navbar';
import Reminder from './components/reminder/reminder';

function fetchReminders() {
    return fetch('http://localhost:4000/reminders')
        .then(response => {
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            return response.json();
        })
        .catch(error => {
            console.error('There was a problem with the fetch operation:', error);
        });
}

function App() {
    const [reminders, setReminders] = useState([]);
    useEffect(() => {
        console.log(fetchReminders())
        fetchReminders().then(data => {
            setReminders(data.map((reminder, index) => {
                return <Reminder reminderName={reminder.title} reminderDescription={reminder.message} />
            }))
        })
    }, []);
    return (
        <div className="App">
            <Navbar />
            <ul id={"remindersWrapper"}>
                <Reminder reminderDescription={"hi"} reminderName={"h"}/>
                {reminders}
            </ul>
        </div>
    );
}

export default App;
