import './popup.css'
import popupContext from "../contexts/popupContext";
import popupSave from "./popups/popupSave";

function Popup(props) {
    const { popupState, setPopupState, popupType, setPopupType } = popupContext;

    if (props.popupState === false) {
        return null;
    }

    switch (props.popupType) {
        default:
            return null
        case 'save':
            return (
                <div className={"popupWrapper"}>
                    <popupSave popupState={props.popupState} setPopupState={props.setPopupState}/>
                </div>
            );
        case 'confirmation':
            return (
                <div className={"popup confirmationPopup"}></div>
            );

        case 'error':
            return (
                <div className={"popup errorPopup"}></div>
            );
    }
}

export default Popup;
