import './popup.css'
import popupContext from "../contexts/generalContext";
import PopupSave from "./popups/popupSave";
import {useContext} from "react";
import PopupDeleteConfirm from "./popups/popupDeleteConfirm";

function Popup() {
    const { popupState, setPopupState, popupType, setPopupType, reminderToDelete, setReminderToDelete } = useContext(popupContext);

    if (popupState === false) {
        return null;
    } else {
        switch (popupType) {
            default:
                return null
            case 'saveConfirmation':
                return (
                    <div id={"popupWrapper"}>
                        <PopupSave popupState={popupState} setPopupState={setPopupState}/>
                    </div>
                );
            case 'deleteConfirmation':
                return (
                    <div id={"popupWrapper"}>
                        <PopupDeleteConfirm reminderToDelete={reminderToDelete}/>
                    </div>
                );
            case 'error':
                return (
                    <div className={"popup errorPopup"}></div>
                );
        }
    }
}

export default Popup;
