const fs = require('fs');
const path = require('path');
const os = require('os');
import './App.css';
import {PlusIcon} from '@primer/octicons-react';

function fetchReminders() {
    var remindersPath;

    switch (os.type()) {
        default:
            console.error('Unsupported OS');
            break;
        case 'Windows_NT':
            remindersPath = path.join(os.homedir(), 'AppData', 'Roaming', 'Reminders', 'reminders.json');
            break;
        case 'Darwin':
            remindersPath = path.join(os.homedir(), '.config', 'Reminders', 'reminders.json');
            break;
        case 'Linux':
            remindersPath = path.join(os.homedir(), '.config', 'Reminders', 'reminders.json');
            break;
    }

    console.log(os.type())


    fs.readFile(remindersPath, 'utf8', (err, data) => {
        if (err) {
            console.error(`Error reading file from disk: ${err}`);
            return;
        }
        return JSON.parse(data);
    });
}

fetchReminders();

function App() {
  return (
    <div className="App">
        <div className="navbar" id="top_navbar">
            <div id="top_navbar_left">
                <h1 id="title">Reminders.py</h1>
                <p>but it's built in react?</p>
            </div>
            <div id="top_navbar_right">
                <button className="button"><PlusIcon fill={"#cfcccc"} size={24} verticalAlign={"middle"}/></button>
            </div>
        </div>
        <ul id="reminders">
            {}
        </ul>
    </div>
  );
}

export default App;
