import { v4 } from "uuid";
import { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/navbar/navbar';
import Reminder from './components/reminder/reminder';
import Popup from './components/popup/popup';
import PopupContext from "./components/contexts/popupContext";

function fetchReminders() {
    return fetch('http://localhost:4000/reminders')
        .then(response => {
              return response.json().then(data => {
                  return data;
              }).catch(err => {
                  console.error("Error parsing JSON: " + err);
              });
        })
}

function App() {
    const [remindersJson, setRemindersJson] = useState(); // [name: message
    const [reminders, setReminders] = useState([]);
    const [popupState, setPopupState] = useState(true);
    const [popupType, setPopupType] = useState("confirmation");
    const [reminderToDelete, setReminderToDelete] = useState("");
    const [queue, setQueue] = useState([]);

    function addToQueue(item) {
        let newQueue = queue;
        newQueue.push(item);
        setQueue(newQueue);
    }

    function convertJSONToReminders(jsonObj) {
        let reminders = [];

        console.log(jsonObj);
        Object.keys(jsonObj).forEach((key, i) => {
            const name = key;
            const dataValues = Object.values(jsonObj)[i];
            const id = v4();
            reminders.push(<Reminder reminderName={name} reminderId={id} key={id} reminderDescription={dataValues.message}/>);
        })

        return reminders;
    }

    useEffect(() => {
        fetchReminders().then(data => {
            setRemindersJson(data);
            console.log(`remindersJson: ${remindersJson}`)
            setReminders(convertJSONToReminders(data));
        }).catch(err => {
            console.error("Error fetching reminders: " + err);
        });
    }, []);

    useEffect(() => {
        console.log("remindersJson changed")
        setReminders(convertJSONToReminders(remindersJson));
    }, [remindersJson])

    window.addEventListener("beforeunload", (e) => {
        if (queue.length > 0) {
            e.preventDefault();
        }
    })

    return (
        <PopupContext.Provider value={{popupState, setPopupState, popupType, setPopupType, reminderToDelete, setReminderToDelete, addToQueue, setQueue, queue, reminders, setReminders, remindersJson, setRemindersJson}}>
            <div className="App">
                <div id={"main"}>
                    <Navbar setPopupState={setPopupState} popupState={popupState}/>
                    <ul id={"remindersWrapper"}>
                        <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                        <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                        <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                        {reminders}
                    </ul>
                </div>
                <Popup/>
            </div>
        </PopupContext.Provider>

    );
}

export default App;
