import {useContext} from "react";
import popupContext from "../../contexts/popupContext";

function PopupDeleteConfirm (props) {
    const { reminders, setReminders, popupState, setPopupState, addToQueue } = useContext(popupContext);

    function togglePopupState() {
        setPopupState(!popupState);
    }

    function deleteReminder() {
        addToQueue({type: "delete", reminder: props.reminderToDelete.id});
        setReminders()
        togglePopupState();
    }

    return (
        <div className={"popup confirmationPopup"}>
            <div id={"popupInfo"}>
                <h1 id={"popupTitle"}>Confirmation</h1>
                <p id={"popupDescription"}>Are you sure you want to delete <span id={"reminderToDeleteName"}>{props.reminderToDelete.name}</span>?</p>
            </div>
            <div id={"popupActions"}>
                <button id={"popupDeleteButton"} className={"important-button button"} onClick={() => {

                }}>
                    <span className={"important-button-text"}>Delete</span></button>
                <button id={"popupCancelButton"} className={"button"} onClick={togglePopupState}><span
                    className={"important-button-text"}>Cancel</span></button>
            </div>
        </div>
    );
}

export default PopupDeleteConfirm;
