import { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/navbar/navbar';
import Reminder from './components/reminder/reminder';
import Popup from './components/popup/popup';

function fetchReminders() {
    return fetch('http://localhost:4000/reminders')
        .then(response => {
              return response.json().then(data => {
                  return data;
              }).catch(err => {
                  console.error("Error parsing JSON: " + err);
              });
        })
}

function App() {
    const [reminders, setReminders] = useState([]);
    const [popupState, setPopupState] = useState(true);
    const [popupType, setPopupType] = useState("")

    useEffect(() => {
        let newReminders = [];
        fetchReminders().then(data => {
            console.log(`Data: ${data}, Keys: ${Object.keys(data)}`)
            for (let i = 0; i < Object.keys(data).length; i++) {
                let name = Object.keys(data)[i];
                let dataValues = Object.values(data)[i];
                newReminders.push(<Reminder reminderName={name} reminderDescription={dataValues.message}/>);
            }
        }).then(() => {
            setReminders(newReminders);
        }).catch(err => {
            console.error("Error fetching reminders: " + err);
        });
    }, []);

    console.log(reminders)

    return (
        <div className="App">
            <div id={"main"}>
                <Navbar setPopupState={setPopupState()}/>
                <ul id={"remindersWrapper"}>
                    <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                    <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                    <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                    {reminders}
                </ul>
            </div>
            <Popup popupState={popupState} setPopupState={setPopupState} popupType={popupType}/>
        </div>
    );
}

export default App;
