import { v4 } from "uuid";
import { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/navbar/navbar';
import Reminder from './components/reminder/reminder';
import Popup from './components/popup/popup';
import PopupContext from "./components/contexts/popupContext";

function fetchReminders() {
    return fetch('http://localhost:4000/reminders')
        .then(response => {
              return response.json().then(data => {
                  return data;
              }).catch(err => {
                  console.error("Error parsing JSON: " + err);
              });
        })
}

function App() {
    const [reminders, setReminders] = useState([]);
    const [popupState, setPopupState] = useState(true);
    const [popupType, setPopupType] = useState("confirmation");
    const [reminderToDelete, setReminderToDelete] = useState("");
    const [queue, setQueue] = useState([]);

    function addToQueue(item) {
        let newQueue = queue;
        newQueue.push(item);
        setQueue(newQueue);
    }

    useEffect(() => {
        let newReminders = [];
        fetchReminders().then(data => {
            console.log(`Data: ${data}, Keys: ${Object.keys(data)}`)
            for (let i = 0; i < Object.keys(data).length; i++) {
                const name = Object.keys(data)[i];
                const dataValues = Object.values(data)[i];
                const id = v4();
                newReminders.push(<Reminder reminderName={name} reminderId={v4()} reminderDescription={dataValues.message}/>);
            }
        }).then(() => {
            setReminders(newReminders);
        }).catch(err => {
            console.error("Error fetching reminders: " + err);
        });
    }, []);

    return (
        <PopupContext.Provider value={{popupState, setPopupState, popupType, setPopupType, reminderToDelete, setReminderToDelete, addToQueue, setQueue, queue}}>
            <div className="App">
                <div id={"main"}>
                    <Navbar setPopupState={setPopupState} popupState={popupState}/>
                    <ul id={"remindersWrapper"}>
                        <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                        <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                        <Reminder reminderDescription={"Test"} reminderName={"This is a test reminder object"}/>
                        {reminders}
                    </ul>
                </div>
                <Popup/>
            </div>
        </PopupContext.Provider>

    );
}

export default App;
