import { useContext } from "react";
import popupContext from "../../contexts/generalContext";

function PopupCreate() {
    const { togglePopupState } = useContext(popupContext);

    function createReminder(reminderJson) {
        const options = {
            'method': 'POST',
            'headers': {
                'Content-Type': 'application/json'
            },
            'body': JSON.stringify(reminderJson)
        }

        fetch('http://localhost:4000/remindersCreate', options)
            .then(response => {
                togglePopupState(null);
            })
            .catch(err => {
                console.error("Error saving reminders: " + err);
            });

    }

    return (
        <div className={"popup"} id={"createPopup"}>
            <div id={"popupInfo"}>
                <h1 id={"popupTitle"}>Create Reminder</h1>
                <input className={"input popupInput"} id={"reminderName"} placeholder={"W"}></input>
                <input className={"input popupInput"} id={"notificationTitle"}></input>
                <input className={"input popupInput"} id={"notificationMessage"}></input>
                <input className={"input popupInput"} id={"time"}></input>
            </div>
            <div id={"popupActions"}>
            <button id={"popupCreateButton"} className={"important-button button"} onClick={() => {
                    createReminder({name: "New Reminder", message: "New Reminder Message"})
                }}><i
                    className={"fi fi-sr-plus"}/><span className={"important-button-text"}>Create</span></button>
                <button id={"popupCancelButton"} className={"button"} onClick={togglePopupState}><span
                    className={"important-button-text"}>Cancel</span></button>
            </div>
        </div>
    );

}

export default PopupCreate;
