import {KebabHorizontalIcon, XIcon} from "@primer/octicons-react";
import './reminder.css';

function Reminder (props) {
    return (
        <li className={"reminder"}>
            <div className={"reminderInfo"}>
                <h2 className={"reminderTitle"}>{props.reminderName}</h2>
                <p className={"reminderDescription"}><span className={"descriptionPrefix"}>Remind me to:</span> {props.reminderDescription}</p>
            </div>
            <div className={"reminderActions"}>
                <button className={"button"}><KebabHorizontalIcon size={16}
                                                                  fill={"#cfcccc"}
                                                                  verticalAlign={"middle"}
                                                                  className={"icon"}/></button>
                <button className={"button"}><XIcon size={16}
                                                    fill={"#cfcccc"}
                                                    verticalAlign={"middle"}
                                                    className={"icon"}/></button>
            </div>
        </li>
    );
}

export default Reminder;
