import './popup.css'
import popupContext from "../contexts/popupContext";
import popupSave from "./popups/popupSave";
import {useContext} from "react";

function Popup() {
    const { popupState, setPopupState, popupType, setPopupType } = useContext(popupContext);

    if (popupState === false) {
        return null;
    } else {
        switch (popupType) {
            default:
                return null
            case 'save':
                return (
                    <div className={"popupWrapper"}>
                        <popupSave popupState={popupState} setPopupState={setPopupState}/>
                    </div>
                );
            case 'confirmation':
                return (
                    <div className={"popup confirmationPopup"}></div>
                );

            case 'error':
                return (
                    <div className={"popup errorPopup"}></div>
                );
        }
    }
}

export default Popup;
