            </div>
            <div id={"popupActions"}>
                <button id={"popupSaveButton"} className={"important-button button"}>
                    <span className={"important-button-text"}>Yes</span></button>
                <button id={"popupCancelButton"} className={"button"} onClick={togglePopupState}><span
                    className={"important-button-text"}>Cancel</span></button>
            </div>
        </div>
    );
}

export default PopupConfirmation;
